package com.example.myapplication.ui.search;import android.content.Context;import android.os.Bundle;import androidx.fragment.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.view.inputmethod.InputMethodManager;import androidx.navigation.fragment.NavHostFragment;import com.example.myapplication.R;import com.example.myapplication.databinding.FragmentSearchBinding;public class SearchFragment extends Fragment {    private FragmentSearchBinding binding;    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {        binding = FragmentSearchBinding.inflate(inflater, container, false);        return binding.getRoot();    }    @Override    public void onViewCreated(View view, Bundle savedInstanceState) {        super.onViewCreated(view, savedInstanceState);        binding.svSearchTours.setIconifiedByDefault(false);        binding.svSearchTours.setIconified(false);        binding.svSearchTours.requestFocus();        binding.svSearchTours.postDelayed(new Runnable() {            @Override            public void run() {                InputMethodManager imm = (InputMethodManager) requireContext().getSystemService(Context.INPUT_METHOD_SERVICE);                imm.showSoftInput(binding.svSearchTours.findViewById(androidx.appcompat.R.id.search_src_text), InputMethodManager.SHOW_IMPLICIT);            }        }, 200);        binding.btnCancel.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                NavHostFragment.findNavController(SearchFragment.this).navigate(R.id.mainFragment);            }        });    }}