package com.example.myapplication.ui.search.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.myapplication.R;import com.example.myapplication.ui.search.model.SearchResult;import java.util.List;public class SearchResultAdapter extends RecyclerView.Adapter<SearchResultViewHolder> {    private List<SearchResult> searchResults;    public SearchResultAdapter(List<SearchResult> searchResults) {        this.searchResults = searchResults;    }    @NonNull    @Override    public SearchResultViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_search_result, parent, false);        return new SearchResultViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull SearchResultViewHolder holder, int position) {        SearchResult searchResult = searchResults.get(position);        holder.bookCoverImageView.setImageResource(searchResult.getBookCover());        holder.bookTitleTextView.setText(searchResult.getBookTitle());        holder.bookAuthorTextView.setText(searchResult.getBookAuthor());        holder.bookDescriptionTextView.setText(searchResult.getBookDescription());    }    @Override    public int getItemCount() {        return searchResults.size();    }    public void updateSearchResults(List<SearchResult> newSearchResults) {        searchResults = newSearchResults;        notifyDataSetChanged();    }}