package com.example.myapplication.ui.search.adapter;import android.view.View;import android.widget.ImageView;import android.widget.TextView;import androidx.recyclerview.widget.RecyclerView;import com.example.myapplication.R;public class SearchResultViewHolder extends RecyclerView.ViewHolder {    ImageView bookCoverImageView;    TextView bookTitleTextView;    TextView bookAuthorTextView;    TextView bookDescriptionTextView;    public SearchResultViewHolder(View itemView) {        super(itemView);        bookCoverImageView = itemView.findViewById(R.id.iv_book_cover);        bookTitleTextView = itemView.findViewById(R.id.tv_book_title);        bookAuthorTextView = itemView.findViewById(R.id.tv_book_author);        bookDescriptionTextView = itemView.findViewById(R.id.tv_book_description);    }}