package com.example.myapplication.ui.search.model;public class SearchResult {    private int bookCoverResId;    private String bookTitle;    private String bookAuthor;    private String bookDescription;    public SearchResult(int bookCoverResId, String bookTitle, String bookAuthor, String bookDescription) {        this.bookCoverResId = bookCoverResId;        this.bookTitle = bookTitle;        this.bookAuthor = bookAuthor;        this.bookDescription = bookDescription;    }    public int getBookCoverResId() {        return bookCoverResId;    }    public String getBookTitle() {        return bookTitle;    }    public String getBookAuthor() {        return bookAuthor;    }    public String getBookDescription() {        return bookDescription;    }}