package com.example.myapplication.ui.search.model;public class SearchResult {    private int bookCover;    private String bookTitle;    private String bookAuthor;    private String bookDescription;    public SearchResult(int bookCover, String bookTitle, String bookAuthor, String bookDescription) {        this.bookCover = bookCover;        this.bookTitle = bookTitle;        this.bookAuthor = bookAuthor;        this.bookDescription = bookDescription;    }    public int getBookCover() {        return bookCover;    }    public String getBookTitle() {        return bookTitle;    }    public String getBookAuthor() {        return bookAuthor;    }    public String getBookDescription() {        return bookDescription;    }}